import { removeHospital, getHospital } from '../helper/hospitalHelper.js';

export const showAlert = (hospitalId) => {

  const hospital = getHospital(hospitalId);

  const swalWithBootstrapButtons = Swal.mixin({
    customClass: {
      confirmButton: 'btn btn-success',
      cancelButton: 'btn btn-danger'

    },
    buttonsStyling: false
  })

  swalWithBootstrapButtons.fire({
    title: `Você tem certeza que deseja deletar o hospital ?`,
    text: "Não será possivel desfazer essa alteração!",
    icon: 'warning',
    showCancelButton: true,
    confirmButtonText: 'Sim, deletar! :(',
    cancelButtonText: 'Não, cancelar! :)',
    reverseButtons: true
  }).then(async(result) => {
    if (result.isConfirmed) {

      removeHospital(hospitalId);

      swalWithBootstrapButtons.fire(
        'Deletedo!',
        'Ah não! Volte sempre :(',
        'success'
      )

      location.reload();

    } else if (
      result.dismiss === Swal.DismissReason.cancel
    ) {
      swalWithBootstrapButtons.fire(
        'Cancelado.',
        'O hospital está a salvo :)',
        'error'
      )
    }
  })
}
